    if (!patient) return;

    try {
      const fileData = await HealthVaultService.fileToBase64(file);
      const record = HealthVaultService.addMedicalRecord(patient.id, {
        fileName: file.name,
        fileType: file.type,
        fileData,
        category: category as any,
