                      </Card>
                    ) : (
                      <div className="space-y-4">
                        {selectedPatient.records.map((record: MedicalRecord) => (
                          <Card key={record.id}>
                            <CardContent className="p-6">
                              <div className="flex justify-between items-start mb-3">
