                          </div>
                          {record.summary && (
                            <div className="bg-blue-50 p-3 rounded-lg">
                              <p className="text-sm text-blue-800">{record.summary}</p>
                            </div>
                          )}
                        </CardContent>
                      </Card>
                    ))}
                  </div>
