<content>import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Progress } from '@/components/ui/progress';
import { Upload, QrCode, FileText, Calendar, LogOut, Plus, User, Settings, Bell, Activity, Heart, Shield, Smartphone, Edit } from 'lucide-react';
import { HealthVaultService, Patient, MedicalRecord } from '@/lib/healthVault';
import { AISummarizer } from '@/lib/aiSummarizer';
import { useNavigate } from 'react-router-dom';
import { toast } from 'sonner';
import FileUpload from '@/components/FileUpload';
import QRGenerator from '@/components/QRGenerator';
import RecordSummary from '@/components/RecordSummary';

type RecordCategory = 'prescription' | 'lab-result' | 'scan' | 'report' | 'other';

export default function PatientDashboard() {
  const navigate = useNavigate();
  const [patient, setPatient] = useState<Patient | null>(null);
  const [records, setRecords] = useState<MedicalRecord[]>([]);
  const [showUpload, setShowUpload] = useState(false);
  const [showQR, setShowQR] = useState(false);

  useEffect(() => {
    const currentUser = HealthVaultService.getCurrentUser();
    if (!currentUser || currentUser.type !== 'patient') {
      navigate('/');
      return;
    }

    const patientData = currentUser.user as Patient;
    setPatient(patientData);
    setRecords(patientData.records || []);
  }, [navigate]);

  const handleLogout = () => {
    HealthVaultService.logout();
    navigate('/');
    toast.success('Logged out successfully');
  };

  const handleFileUpload = async (file: File, category: string) => {
    if (!patient) return;

    try {
      const fileData = await HealthVaultService.fileToBase64(file);
      const record = HealthVaultService.addMedicalRecord(patient.id, {
        fileName: file.name,
        fileType: file.type,
        fileData,
        category: category as RecordCategory,
        summary: AISummarizer.summarizeRecord({
          fileName: file.name,
          category: category as RecordCategory
        } as MedicalRecord)
      });

      if (record) {
        const updatedPatient = HealthVaultService.getPatient(patient.id);
        if (updatedPatient) {
          setPatient(updatedPatient);
          setRecords(updatedPatient.records);
        }
        toast.success('Medical record uploaded successfully!');
        setShowUpload(false);
      }
    } catch (error) {
      toast.error('Failed to upload file. Please try again.');
    }
  };

  const getCategoryColor = (category: string) => {
    const colors = {
      'prescription': 'bg-blue-100 text-blue-800',
      'lab-result': 'bg-green-100 text-green-800',
      'scan': 'bg-purple-100 text-purple-800',
      'report': 'bg-orange-100 text-orange-800',
      'other': 'bg-gray-100 text-gray-800'
    };
    return colors[category as keyof typeof colors] || colors.other;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getProfileCompleteness = () => {
    if (!patient) return 0;
    let completeness = 0;
    if (patient.name) completeness += 20;
    if (patient.email) completeness += 20;
    if (patient.phone) completeness += 20;
    if (patient.dateOfBirth) completeness += 20;
    if (patient.emergencyContact) completeness += 20;
    return completeness;
  };

  if (!patient) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600"></div>
      </div>
    );
  }

